import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useVoiceRecognition } from '../hooks/useVoiceRecognition';
import { useTextToSpeech } from '../hooks/useTextToSpeech';
import { useChatBot } from '../hooks/useChatBot';
import { Message } from '../types';
import '../styles/ChatInterface.css';

const ChatInterface: React.FC = () => {
  const [messages, setMessages] = useState<Message[]>([]);
  const [isListening, setIsListening] = useState(false);
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [pendingTranscript, setPendingTranscript] = useState('');
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const { 
    transcript, 
    finalTranscript,
    isListening: voiceIsListening, 
    startListening, 
    stopListening,
    error: voiceError
  } = useVoiceRecognition();

  const { 
    speak, 
    stopSpeaking, 
    isSpeaking: ttsIsSpeaking 
  } = useTextToSpeech();

  const { 
    sendMessage, 
    isLoading 
  } = useChatBot();

  useEffect(() => {
    setIsListening(voiceIsListening);
  }, [voiceIsListening]);

  useEffect(() => {
    setIsSpeaking(ttsIsSpeaking);
  }, [ttsIsSpeaking]);

  // Update pending transcript while listening
  useEffect(() => {
    if (isListening) {
      setPendingTranscript(transcript);
    }
  }, [transcript, isListening]);

  // Process final transcript when listening stops
  useEffect(() => {
    if (!isListening && finalTranscript.trim()) {
      handleSendMessage(finalTranscript);
      setPendingTranscript('');
    }
  }, [isListening, finalTranscript]);

  const handleSendMessage = useCallback(async (text: string) => {
    if (!text.trim()) return;

    const userMessage: Message = {
      id: Date.now(),
      text,
      sender: 'user',
      timestamp: new Date()
    };

    setMessages(prev => [...prev, userMessage]);

    try {
      const response = await sendMessage(text);
      const botMessage: Message = {
        id: Date.now() + 1,
        text: response,
        sender: 'bot',
        timestamp: new Date()
      };

      setMessages(prev => [...prev, botMessage]);
      speak(response);
    } catch (error) {
      console.error('Error sending message:', error);
    }
  }, [sendMessage, speak]);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const toggleListening = () => {
    if (isListening) {
      stopListening();
    } else {
      startListening();
    }
  };

  const handleReset = () => {
    setMessages([]);
    stopSpeaking();
    setPendingTranscript('');
  };

  // Icons as SVG components
  const MicIcon = () => (
    <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
      <path d="M12 14c1.66 0 3-1.34 3-3V5c0-1.66-1.34-3-3-3S9 3.34 9 5v6c0 1.66 1.34 3 3 3z"/>
      <path d="M17 11c0 2.76-2.24 5-5 5s-5-2.24-5-5H5c0 3.53 2.61 6.43 6 6.92V21h2v-3.08c3.39-.49 6-3.39 6-6.92h-2z"/>
    </svg>
  );

  const StopIcon = () => (
    <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
      <path d="M6 6h12v12H6z"/>
    </svg>
  );

  const ResetIcon = () => (
    <svg width="20" height="20" viewBox="0 0 24 24" fill="currentColor">
      <path d="M12 5V1L7 6l5 5V7c3.31 0 6 2.69 6 6s-2.69 6-6 6-6-2.69-6-6H4c0 4.42 3.58 8 8 8s8-3.58 8-8-3.58-8-8-8z"/>
    </svg>
  );

  const BotIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
      <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z"/>
    </svg>
  );

  const UserIcon = () => (
    <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
      <path d="M12 12c2.21 0 4-1.79 4-4s-1.79-4-4-4-4 1.79-4 4 1.79 4 4 4zm0 2c-2.67 0-8 1.34-8 4v2h16v-2c0-2.66-5.33-4-8-4z"/>
    </svg>
  );

  return (
    <div className="chat-container fade-in">
      <header className="chat-header glass">
        <h1>🤖 ChatGPT Voice Bot</h1>
        <p className="disclaimer">Ask me anything about myself in a thoughtful, introspective way</p>
        <div className="voice-status">
          <div className={`status-dot ${isListening ? 'listening' : ''}`}></div>
          <span>{isListening ? 'Listening...' : 'Ready to chat'}</span>
        </div>
      </header>

      <div className="messages-container glass">
        {messages.length === 0 && !isListening ? (
          <div className="empty-state">
            <h3>👋 Welcome!</h3>
            <p>Click the microphone button and ask me anything about my background, skills, or experiences.</p>
            <p>Try asking: "Tell me about your background" or "What's your #1 superpower?"</p>
          </div>
        ) : (
          <>
            {messages.map((message) => (
              <div
                key={message.id}
                className={`message ${message.sender === 'user' ? 'user-message' : 'bot-message'}`}
              >
                <div className="message-content">
                  <div style={{ display: 'flex', alignItems: 'flex-start', gap: '8px' }}>
                    {message.sender === 'user' ? <UserIcon /> : <BotIcon />}
                    <div style={{ flex: 1 }}>
                      <p>{message.text}</p>
                      <span className="timestamp">
                        {message.timestamp.toLocaleTimeString()}
                      </span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
            
            {/* Show pending transcript while listening */}
            {isListening && pendingTranscript && (
              <div className="message user-message">
                <div className="message-content" style={{ opacity: 0.7, fontStyle: 'italic' }}>
                  <div style={{ display: 'flex', alignItems: 'flex-start', gap: '8px' }}>
                    <UserIcon />
                    <div style={{ flex: 1 }}>
                      <p>"{pendingTranscript}"</p>
                      <span className="timestamp">Listening...</span>
                    </div>
                  </div>
                </div>
              </div>
            )}
          </>
        )}
        <div ref={messagesEndRef} />
      </div>

      <div className="controls glass">
        <button
          className={`mic-button ${isListening ? 'listening' : ''}`}
          onClick={toggleListening}
          disabled={isLoading || isSpeaking}
        >
          {isListening ? <StopIcon /> : <MicIcon />}
          {isListening ? 'Stop Listening' : 'Start Listening'}
        </button>

        <button
          className="reset-button"
          onClick={handleReset}
          disabled={isLoading || isSpeaking}
        >
          <ResetIcon />
          Reset Chat
        </button>
      </div>

      {isLoading && (
        <div className="loading-indicator glass">
          <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '10px' }}>
            <div className="status-dot" style={{ animation: 'pulse 1s infinite' }}></div>
            Thinking...
          </div>
        </div>
      )}

      {voiceError && (
        <div style={{ 
          background: 'rgba(245, 101, 101, 0.1)', 
          border: '1px solid rgba(245, 101, 101, 0.3)',
          borderRadius: '8px',
          padding: '12px',
          marginTop: '10px',
          color: '#e53e3e',
          fontSize: '0.9rem',
          textAlign: 'center'
        }}>
          ⚠️ {voiceError}
        </div>
      )}
    </div>
  );
};

export default ChatInterface;
