import { useState, useCallback } from 'react';
import { userProfile } from '../data/userProfile';
import { botResponses } from '../data/botResponses';

interface ChatBotState {
  sendMessage: (message: string) => Promise<string>;
  isLoading: boolean;
  error: string | null;
}

// Backend API endpoint
const API_URL = 'http://localhost:3001/api/chat';

// Function to find the best matching predefined response
const findBestMatch = (userQuestion: string): string | null => {
  const question = userQuestion.toLowerCase().trim();
  
  // Direct matches
  const directMatch = botResponses.find(response => 
    response.question.toLowerCase().trim() === question
  );
  
  if (directMatch) {
    return directMatch.answer;
  }
  
  // Partial matches using keywords
  const keywords = question.split(' ');
  const bestMatch = botResponses.find(response => {
    const responseKeywords = response.question.toLowerCase().split(' ');
    const matchScore = keywords.filter(keyword => 
      responseKeywords.some(responseKeyword => 
        responseKeyword.includes(keyword) || keyword.includes(responseKeyword)
      )
    ).length;
    return matchScore >= Math.ceil(keywords.length * 0.6); // 60% keyword match
  });
  
  return bestMatch ? bestMatch.answer : null;
};

export const useChatBot = (): ChatBotState => {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const sendMessage = useCallback(async (message: string): Promise<string> => {
    setIsLoading(true);
    setError(null);

    try {
      const response = await fetch(API_URL, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ message })
      });

      if (!response.ok) {
        throw new Error('Failed to get response from the server');
      }

      const data = await response.json();
      
      if (data.error) {
        throw new Error(data.error);
      }

      return data.response;
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'An unknown error occurred';
      setError(errorMessage);
      return "I'm sorry, I encountered an error while processing your request. Please try again.";
    } finally {
      setIsLoading(false);
    }
  }, []);

  return {
    sendMessage,
    isLoading,
    error
  };
};
